import shop from '../../api/shop'

const state = () => ({
    providerProducts : []
})

const getters = {}

const actions = {
    async getProviderProducts ({state, commit, rootState}) {
        const products  = await shop.getProducts(rootState.providers.provider)
        commit('setProviderProducts', products)
    },
    async getTest() {
        console.log('AH');
    }
}

const mutations = {
    setProviderProducts (state, products) {
        state.providerProducts = products
    },
    decrementProductInventory(state, {id}) {
        product = providerProducts.find((product) => product.id === id)
        product.inventory--
    }
}

export default {
    namespaced: true,
    state,
    getters,
    actions,
    mutations
  }
