import shop from '../../api/shop'

const state = () => ({
    items : [],
    checkoutStatus: null
})

const getters = {
    cartProducts(state, rootState) {
        return state.items.map(({id, quantity}) => {
            const products = rootState.products.providerProducts.find(product => product.idProduct === id)
            return {
                id : products.id,
                name : products.name,
                price : products.price,
                quantity
            }
        })
    },

    cartTotalPrice (getters) {
        return getters.cartProducts.reduce((product, total) => {
            return total + product.price * product.quantity
        }, 0)
    }
}

const actions = {
    async checkout ({commit, state}, products) {
        const savedCartItems = [...state.items]
        commit('setCheckoutStatus', null)
        commit('setCartItems', { items : []})
        try {
            await shop.buyProducts(products)
            commit('setCheckoutStatus', 'succesful')
        } catch(e) {
            console.error(e)
            commit('setCheckoutStatus', 'failed')
            commit('setCartItems', {items : savedCartItems})
        }
    },

    addProductToCart ({state, commit}, product) {
        commit('setCheckoutStatus', null)
        if (product.inventory > 0) {
            const cartItem = state.items.find(item => item.id === product.id)
            if (!cartItem) {
                commit('pushProductToCart', {id : product.id})
            } else {
                commit('incrementItemQuantity', { id : product.id})
            }
        }
        commit('products/decrementProductInventory', { id : product.id}, {root : true})
    }
}

const mutations = {
    pushProductToCart (state, { id }) {
        state.items.push({
            id,
            quantity: 1
        })
    },

    incrementItemQuantity (state, { id }) {
        const cartItem = state.items.find(item => item.id === id)
        cartItem.quantity++
    },

    setCartItems (state, {items}) {
        state.items = items
    },

    setCheckoutStatus (state, status) {
        state.checkoutStatus = status
    }
}

export default {
    namespaced: true,
    state,
    getters,
    actions,
    mutations
  }
