import shop from '../../api/shop'

const state = () => ({
    searchInput : ''
})

const getters = {}

const actions = {
    getNewProducts ({state, commit, rootState}) {

        rootState.products.setProviderProducts(shop.getProducts(rootState.providers.selectedProvider()))
    }
}

const mutations = {}

export default {
    namespaced: true,
    state,
    getters,
    actions,
    mutations
  }
