// discussion avec le serveur

const _products = [
    {'idProduct' : 1, 'nameProduct' : 'product 1', 'price' : 5.00, 'idProvider' : '1', 'inventory' : '5'},
    {'idProduct' : 2, 'nameProduct' : 'product 2', 'price' : 4.00, 'idProvider' : '2', 'inventory' : '5'},
    {'idProduct' : 3, 'nameProduct' : 'product 3', 'price' : 7.00, 'idProvider' : '2', 'inventory' : '5'},
    {'idProduct' : 4, 'nameProduct' : 'product 4', 'price' : 8.00, 'idProvider' : '3', 'inventory' : '5'},
    {'idProduct' : 5, 'nameProduct' : 'product 5', 'price' : 1.00, 'idProvider' : '3', 'inventory' : '5'},
    {'idProduct' : 6, 'nameProduct' : 'product 6', 'price' : 6.00, 'idProvider' : '3', 'inventory' : '5'}
]

const _providers = [
    {'id' : 1, 'name' : 'provider 1'},
    {'id' : 2, 'name' : 'provider 2'},
    {'id' : 3, 'name' : 'provider 3'},
]

export default {
    async getProducts(providerId, searchInput) {
        await wait(100)
        const providerProducts =  _products.find(product => product.idProvider === providerId)
        return providerProducts
        //return providerProducts.find(product => product.nameProduct.includes(searchInput))
    },

    async buyProducts(product) {
        await wait(100)
        if((Math.random() > 0.2 || navigator.webdriver)) {
            return 
        } else {
            throw new Error('Checkout error')
        }
    },

    async getProviders() {
        await wait(100)
        return _providers
    }
}

function wait (ms) {
    return new Promise(resolve => {
        setTimeout(resolve, ms)
    })
}
