import shop from '../../api/shop'

const state = () => ({
    items : [],
    checkoutStatus: null
})

const getters = {
    getCartProducts(state, getters, rootState) {
        const providerProducts = rootState.products.providerProducts
        const cartProducts = []
        state.items.forEach(item => {
            const product = providerProducts.find(product => product.idProduct === item.id)
            cartProducts.push({
                'nameProduct' : product.nameProduct,
                'price' : product.price,
                'quantity' : item.quantity
            })
        });
        return cartProducts
    }
}

const actions = {
    async checkout ({commit, state}, products) {
        const savedCartItems = [...state.items]
        commit('setCheckoutStatus', null)
        commit('setCartItems', { items : []})
        try {
            await shop.buyProducts(products)
            commit('setCheckoutStatus', 'succesful')
        } catch(e) {
            console.error(e)
            commit('setCheckoutStatus', 'failed')
            commit('setCartItems', {items : savedCartItems})
        }
    },

    addProductToCart({state, commit}, product) {
        const cartProduct = [state.items.find(item => item.id = product.idProduct)]
        if (cartProduct.lenght > 0) {
            commit('incrementItemQuantity', product)
        } else {
            commit('pushProductToCart', product)
        }
    },
}

const mutations = {
    pushProductToCart (state, product) {
        state.items.push({
            'id' : product.idProduct,
            'quantity' : 1
        })
    },

    incrementItemQuantity (state, product) {
        const cartItem = state.items.find(item => item.id === product.idProduct)
        cartItem.quantity++
    },

    setCartItems (state, { items }) {
        state.items = items
    },

    setCheckoutStatus (state, status) {
        state.checkoutStatus = status
    }
}

export default {
    namespaced: true,
    state,
    getters,
    actions,
    mutations
  }
