import shop from '../../api/shop'

const state = () => ({
    providerProducts : []
})

const getters = {
    getProviderProducts (state) {
        return state.providerProducts
    }
}

const actions = {
    updateProviderProducts ({commit, rootState}) {
        searchInput = rootState.search.searchInput
        provider = rootState.providers.selectedProvider
        products = shop.getProducts(provider.id, searchInput)
        commit('setProviderProducts', products)
    }
}

const mutations = {
    setProviderProducts ({state}, products) {
        state.providerProducts = products
    }
}

export default {
    namespaced: true,
    state,
    getters,
    actions,
    mutations
  }
