import shop from '../../api/shop'

const state = () => ({
    searchInput : ''
})

const getters = {}

const actions = {
    getNewProducts ({state, commit, rootState}) {
        commit('changeSearchInput')
        provider = rootState.providers.selectedProvider()
        rootState.products.setProviderProducts(shop.getProducts(state.searchInput))
    }
}

const mutations = {
    changeSearchInput (state) {
        state.searchInput = this.search.toLowerCase()
    }
}
