import shop from '../../api/shop'

const state = () => ({
    allProviders : [],
    actualProvider : '1'
})

const getters = {
    selectedProvider(state) {
        return state.actualProvider
    }
}

const actions = {
    async getAllProviders ({commit}) {
        const allProviders = await shop.getProviders()
        commit('setAllProviders', allProviders)
    }
}

const mutations = {
    changeProvider (state, newProvider) {
        state.actualProvider = state.allProviders.find(provider => provider.id === newProvider.id)
    },

    setAllProviders (state, providers) {
        state.allProviders = providers
    }
}

export default {
    namespaced: true,
    state,
    getters,
    actions,
    mutations
  }
