import shop from '../../api/shop'

const state = () => ({
    allProviders : [],
    Actuallprovider : null
})

const getters = {
    selectedProvider : (state, getters) => {
        return state.provider
    }
}

const actions = {
    async getAllProviders ({commit}) {
        allProviders = await shop.getProviders()
        commit('setAllProviders', allProviders)
    }
}

const mutations = {
    changeProvider (state, newProvider) {
        state.Actuallprovider = state.allProviders.find(provider => provider.id === newProvider.id)
    },

    setAllProviders (state, providers) {
        state.allProviders = providers
    }
}
