import shop from '../api/shop'

const state = () => ({
    allProducts :[],
    providerProducts : []
})

const getters = {
    getProviderProducts (state) {
        return state.providerProducts
    }
}

const actions = {
    updateProviderProducts ({commit, rootState}) {
        const searchInput = rootState.search.searchInput
        const provider = rootState.providers.selectedProvider
        const products = shop.getProducts(provider.id, searchInput)
        commit('setProviderProducts', products)
    },

    updateAllProducts ({commit}) {
        const products = shop.getAllProducts()
        commit('setAllProducts', products)
    }
}

const mutations = {
    setProviderProducts (state, products) {
        state.providerProducts = products
    },

    setAllProducts (state, products) {
        state.allProducts = products
    },

    decrementItemQuantity(state, product) {
        const productToDecrement = state.product.find(item => item.id === product.id)
        productToDecrement.quantity--
    }
}

export default {
    namespaced: true,
    state,
    getters,
    actions,
    mutations
  }
