import { all } from 'core-js/fn/promise'
import shop from '../../api/shop'

const state = () => ({
    allProviders : [],
    selectedProvider : allProviders[0]
})

const getters = {

}

const actions = {
    apiGetAllProviders ({commit}) {
        commit('setAllProviders', shop.getProviders())
    },
    changeProvider({commit}, provider) {
        commit('setSelectedProvider', provider)
    }
}

const mutations = {
    setAllProviders (state, providers) {
        state.allProviders = providers
    },
    setSelectedProvider(state, provider) {
        state.selectedProvider = provider
    }
}

export default {
    namespaced: true,
    state,
    getters,
    actions,
    mutations
  }
