const _products = [
    {'idProduct' : '1', 'nameProduct' : 'produit 1', 'price' : '5.00', 'idProvider' : '1', 'inventory' : '5'},
    {'idProduct' : '2', 'nameProduct' : 'produit 2', 'price' : '4.00', 'idProvider' : '2', 'inventory' : '5'},
    {'idProduct' : '3', 'nameProduct' : 'produit 3', 'price' : '7.00', 'idProvider' : '2', 'inventory' : '5'},
    {'idProduct' : '4', 'nameProduct' : 'produit 4', 'price' : '8.00', 'idProvider' : '3', 'inventory' : '5'},
    {'idProduct' : '5', 'nameProduct' : 'produit 5', 'price' : '1.00', 'idProvider' : '3', 'inventory' : '5'},
    {'idProduct' : '6', 'nameProduct' : 'produit 6', 'price' : '6.00', 'idProvider' : '3', 'inventory' : '5'}
]

const _providers = [
    {'id' : '1', 'name' : 'fournisseur 1'},
    {'id' : '2', 'name' : 'fournisseur 2'},
    {'id' : '3', 'name' : 'fournissuer 3'},
]

export default {
    getProducts(providerId, searchInput) {
        //await wait(100)
        const providerProducts = []
        _products.forEach(product => {
            if (product.idProvider === providerId) {
                if (product.nameProduct.includes(searchInput)) {
                    providerProducts.push(product)
                }
            }
        });
        return providerProducts
    },

    getAllProducts() {
        return _products
    },

    buyProducts(product) {
        //await wait(100)
        if((Math.random() > 0.2 || navigator.webdriver)) {
            return 
        } else {
            throw new Error('Checkout error')
        }
    },

    getProviders() {
        //await wait(100)
        return _providers
    }
}

function wait (ms) {
    return new Promise(resolve => {
        setTimeout(resolve, ms)
    })
}
