import shop from '../../api/shop'

const state = () => ({
    searchInput : ''
})

const getters = {}

const actions = {
    getNewProducts ({state, commit, rootState}) {
        const provider = rootState.providers.selectedProvider()
        rootState.products.setProviderProducts(shop.getProducts(state.searchInput))
    }
}

const mutations = {}

export default {
    namespaced: true,
    state,
    getters,
    actions,
    mutations
  }
